import unittest
from main import transcribing_dna_into_rna


class TestTranscribingDNAIntoRNA(unittest.TestCase):

    def test_case_01(self):
        test_input = 'GATGGAACTTGACTACGTAAATT'
        expect = 'GAUGGAACUUGACUACGUAAAUU'
        actual = transcribing_dna_into_rna(test_input)

        self.assertEqual(expect, actual)

    def test_case_02(self):
        test_input = ''
        expect = ''

        actual = transcribing_dna_into_rna(test_input)
        self.assertEqual(expect, actual)

if __name__ == '__main__':
    unittest.main()
